<script>
import NavBar from "@/components/navBar.vue";

export default {
  components: {NavBar},
  mounted() {
    window.addEventListener("scroll", this.handleScroll);
  },
  beforeDestroy() {
    window.removeEventListener("scroll", this.handleScroll);
  },
  methods: {
    handleScroll() {
      // Get the SVG element and its position relative to the viewport
      const svgElement = document.querySelector(".shadow");
      const svgRect = svgElement.getBoundingClientRect();
      
      // Check if the bottom of the SVG is within the viewport
      if (svgRect.bottom >= 0) {
        svgElement.classList.add("in-view");
      }
    },
  },
}
</script>

